+'use client';
+
+import { useEffect, useRef } from 'react';
+import * as d3 from 'd3';
+import type { ChartDataPoint } from '@/types/dashboardTypes';
+
+interface BarChartProps {
+  data: ChartDataPoint[];
+  metric?: 'CLS' | 'FCP' | 'LCP' | 'TTFB' | 'INP'; // 막대 차트의 기준이 될 메트릭
+}
+
+export default function BarChart({ data, metric = 'LCP' }: BarChartProps) {
+  const svgRef = useRef<SVGSVGElement>(null);
+
+  useEffect(() => {
+    if (!data.length || !svgRef.current) return;
+
+    const svg = d3.select(svgRef.current);
+    svg.selectAll('*').remove();
+
+    const margin = { top: 20, right: 30, bottom: 40, left: 50 };
+    const width = 600 - margin.left - margin.right;
+    const height = 300 - margin.bottom - margin.top;
+
+    const g = svg
+      .attr('width', width + margin.left + margin.right)
+      .attr('height', height + margin.top + margin.bottom)
+      .append('g')
+      .attr('transform', `translate(${margin.left},${margin.top})`);
+
+    const filteredData = data.filter((d) => d.name === metric);
+    if (!filteredData.length) {
+      g.append('text')
+        .attr('x', width / 2)
+        .attr('y', height / 2)
+        .attr('text-anchor', 'middle')
+        .attr('fill', '#6b7280')
+        .text('데이터가 없습니다.');
+      return;
+    }
+
+    // Create histogram bins for the selected metric
+    const histogram = d3
+      .bin<ChartDataPoint, number>()
+      .value((d) => d.value)
+      .domain(d3.extent(filteredData, (d) => d.value) as [number, number]) // 동적으로 도메인 설정
+      .thresholds(12);
+
+    const bins = histogram(filteredData);
+
+    // Scales
+    const xScale = d3
+      .scaleLinear()
+      .domain(d3.extent(filteredData, (d) => d.value) as [number, number])
+      .range([0, width])
+      .nice(); // 눈금을 보기 좋게 조정
+
+    const yScale = d3
+      .scaleLinear()
+      .domain([0, d3.max(bins, (d) => d.length) || 1])
+      .nice()
+      .range([height, 0]);
+
+    // Add axes
+    g.append('g')
+      .attr('transform', `translate(0,${height})`)
+      .call(
+        d3.axisBottom(xScale).tickFormat((d: d3.NumberValue) => {
+          const value = typeof d === 'number' ? d : Number(d.valueOf());
+          return metric === 'CLS' ? value.toFixed(2) : `${value}ms`;
+        })
+      );
+
+    g.append('g').call(d3.axisLeft(yScale));
+    const thresholds = getMetricThresholds(metric);
+    if (thresholds) {
+      // Good threshold
+      g.append('line')
+        .attr('x1', xScale(thresholds.good))
+        .attr('x2', xScale(thresholds.good))
+        .attr('y1', 0)
+        .attr('y2', height)
+        .attr('stroke', '#10b981')
+        .attr('stroke-dasharray', '5,5')
+        .attr('opacity', 0.7);
+
+      // Poor threshold
+      g.append('line')
+        .attr('x1', xScale(thresholds.poor))
+        .attr('x2', xScale(thresholds.poor))
+        .attr('y1', 0)
+        .attr('y2', height)
+        .attr('stroke', '#ef4444')
+        .attr('stroke-dasharray', '5,5')
+        .attr('opacity', 0.7);
+    }
+
+    // Add bars with color coding
+    g.selectAll('.bar')
+      .data(bins)
+      .enter()
+      .append('rect')
+      .attr('class', 'bar')
+      .attr('x', (d) => xScale(d.x0 || 0))
+      .attr('y', (d) => yScale(d.length))
+      .attr('width', (d) => Math.max(0, xScale(d.x1 || 0) - xScale(d.x0 || 0) - 1))
+      .attr('height', (d) => height - yScale(d.length))
+      .attr('fill', (d) => {
+        const midpoint = ((d.x0 || 0) + (d.x1 || 0)) / 2;
+        const rating = getRatingFromValue(metric, midpoint);
+        return getRatingColor(rating);
+      })
+      .attr('opacity', 0.7)
+      .on('mouseover', function (event, d) {
+        d3.select(this).attr('opacity', 1);
+      })
+      .on('mouseout', function (event, d) {
+        d3.select(this).attr('opacity', 0.7);
+      });
+
+    // Add labels for thresholds
+    if (thresholds) {
+      g.append('text')
+        .attr('x', xScale(thresholds.good))
+        .attr('y', -5)
+        .attr('text-anchor', 'middle')
+        .attr('font-size', '12px')
+        .attr('fill', '#10b981')
+        .text('Good');
+
+      g.append('text')
+        .attr('x', xScale(thresholds.poor))
+        .attr('y', -5)
+        .attr('text-anchor', 'middle')
+        .attr('font-size', '12px')
+        .attr('fill', '#ef4444')
+        .text('Poor');
+    }
+  }, [data, metric]);
+
+  const getMetricThresholds = (metric: string) => {
+    const thresholds: Record<string, { good: number; poor: number }> = {
+      CLS: { good: 0.1, poor: 0.25 },
+      FCP: { good: 1800, poor: 3000 },
+      LCP: { good: 2500, poor: 4000 },
+      TTFB: { good: 800, poor: 1800 },
+      INP: { good: 200, poor: 500 }
+    };
+    return thresholds[metric];
+  };
+
+  const getRatingFromValue = (metric: string, value: number) => {
+    const thresholds = getMetricThresholds(metric);
+    if (!thresholds) return 'good'; // Default
+
+    if (value <= thresholds.good) {
+      return 'good';
+    } else if (value <= thresholds.poor) {
+      return 'needs-improvement';
+    } else {
+      return 'poor';
+    }
+  };
+
+  const getRatingColor = (rating: 'good' | 'needs-improvement' | 'poor') => {
+    switch (rating) {
+      case 'good':
+        return '#10b981';
+      case 'needs-improvement':
+        return '#f59e0b';
+      case 'poor':
+        return '#ef4444';
+      default:
+        return '#6b7280';
+    }
+  };
+
+  return (
+    <div className="w-full overflow-x-auto">
+      <svg ref={svgRef} className="w-full h-[300px]" />
+    </div>
+  );
+}

@@ -0,0 +1,118 @@
+'use client';
+
+import { useEffect, useRef } from 'react';
+import * as d3 from 'd3';
+import type { ChartDataPoint } from '@/types/dashboardTypes';
+
+interface DonutChartProps {
+  data: ChartDataPoint[];
+}
+
+export default function DonutChart({ data }: DonutChartProps) {
+  const svgRef = useRef<SVGSVGElement>(null);
+
+  useEffect(() => {
+    if (!data.length || !svgRef.current) return;
+
+    const svg = d3.select(svgRef.current);
+    svg.selectAll('*').remove();
+
+    const width = 300;
+    const height = 300;
+    const radius = Math.min(width, height) / 2;
+    const innerRadius = radius * 0.6;
+
+    const g = svg
+      .attr('width', width)
+      .attr('height', height)
+      .append('g')
+      .attr('transform', `translate(${width / 2},${height / 2})`);
+
+    // 등급별 데이터 집계
+    const ratingCounts = data.reduce(
+      (acc, d) => {
+        acc[d.rating] = (acc[d.rating] || 0) + 1;
+        return acc;
+      },
+      {} as Record<'good' | 'needs-improvement' | 'poor', number>
+    );
+
+    const chartData = [
+      { label: '좋음', value: ratingCounts.good || 0, color: '#10b981' },
+      { label: '개선필요', value: ratingCounts['needs-improvement'] || 0, color: '#f59e0b' },
+      { label: '나쁨', value: ratingCounts.poor || 0, color: '#ef4444' }
+    ].filter((d) => d.value > 0); // 값이 0인 항목은 제외
+
+    if (!chartData.length) {
+      g.append('text')
+        .attr('x', 0)
+        .attr('y', 0)
+        .attr('text-anchor', 'middle')
+        .attr('fill', '#6b7280')
+        .text('데이터가 없습니다.');
+      return;
+    }
+
+    const pie = d3
+      .pie<(typeof chartData)[0]>()
+      .value((d) => d.value)
+      .sort(null);
+
+    const arc = d3
+      .arc<d3.PieArcDatum<(typeof chartData)[0]>>()
+      .innerRadius(innerRadius)
+      .outerRadius(radius);
+
+    const arcs = g.selectAll('.arc').data(pie(chartData)).enter().append('g').attr('class', 'arc');
+
+    arcs
+      .append('path')
+      .attr('d', arc)
+      .attr('fill', (d) => d.data.color)
+      .attr('opacity', 0.8)
+      .on('mouseover', function (event, d) {
+        d3.select(this).attr('opacity', 1);
+      })
+      .on('mouseout', function (event, d) {
+        d3.select(this).attr('opacity', 0.8);
+      });
+
+    // Add labels
+    arcs
+      .append('text')
+      .attr('transform', (d) => `translate(${arc.centroid(d)})`)
+      .attr('text-anchor', 'middle')
+      .attr('font-size', '12px')
+      .attr('fill', 'white')
+      .attr('font-weight', 'bold')
+      .each(function (d) {
+        const text = d3.select(this);
+        const percentage = ((d.data.value / d3.sum(chartData, (d) => d.value)) * 100).toFixed(1);
+        text.append('tspan').attr('x', 0).attr('dy', '-0.2em').text(d.data.label);
+        text.append('tspan').attr('x', 0).attr('dy', '1.2em').text(`${percentage}%`);
+      });
+
+    // Add center text showing total count
+    const totalCount = d3.sum(chartData, (d) => d.value);
+    g.append('text')
+      .attr('text-anchor', 'middle')
+      .attr('y', -5)
+      .attr('font-size', '24px')
+      .attr('font-weight', 'bold')
+      .attr('fill', '#333')
+      .text(`${totalCount}`);
+
+    g.append('text')
+      .attr('text-anchor', 'middle')
+      .attr('y', 15)
+      .attr('font-size', '12px')
+      .attr('fill', '#6b7280')
+      .text('총 데이터');
+  }, [data]);
+
+  return (
+    <div className="flex justify-center">
+      <svg ref={svgRef} className="w-[300px] h-[300px]" />
+    </div>
+  );
+}

