'use client';

import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import type { ChartDataPoint } from '@/types/dashboardTypes';

interface BarChartProps {
  data: ChartDataPoint[];
  metric?: 'CLS' | 'FCP' | 'LCP' | 'TTFB' | 'INP'; // 막대 차트의 기준이 될 메트릭
}

export default function BarChart({ data, metric = 'LCP' }: BarChartProps) {
  const svgRef = useRef<SVGSVGElement>(null);

  useEffect(() => {
    if (!data.length || !svgRef.current) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    const margin = { top: 20, right: 30, bottom: 40, left: 50 };
    const width = 600 - margin.left - margin.right;
    const height = 300 - margin.bottom - margin.top;

    const g = svg
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    const filteredData = data.filter((d) => d.name === metric);
    if (!filteredData.length) {
      g.append('text')
        .attr('x', width / 2)
        .attr('y', height / 2)
        .attr('text-anchor', 'middle')
        .attr('fill', '#6b7280')
        .text('데이터가 없습니다.');
      return;
    }

    // Create histogram bins for the selected metric
    const histogram = d3
      .bin<ChartDataPoint, number>()
      .value((d) => d.value)
      .domain(d3.extent(filteredData, (d) => d.value) as [number, number]) // 동적으로 도메인 설정
      .thresholds(12);

    const bins = histogram(filteredData);

    // Scales
    const xScale = d3
      .scaleLinear()
      .domain(d3.extent(filteredData, (d) => d.value) as [number, number])
      .range([0, width])
      .nice(); // 눈금을 보기 좋게 조정

    const yScale = d3
      .scaleLinear()
      .domain([0, d3.max(bins, (d) => d.length) || 1])
      .nice()
      .range([height, 0]);

    // Add axes
    g.append('g')
      .attr('transform', `translate(0,${height})`)
      .call(
        d3.axisBottom(xScale).tickFormat((d: d3.NumberValue) => {
          const value = typeof d === 'number' ? d : Number(d.valueOf());
          return metric === 'CLS' ? value.toFixed(2) : `${value}ms`;
        })
      );

    g.append('g').call(d3.axisLeft(yScale));
    const thresholds = getMetricThresholds(metric);
    if (thresholds) {
      // Good threshold
      g.append('line')
        .attr('x1', xScale(thresholds.good))
        .attr('x2', xScale(thresholds.good))
        .attr('y1', 0)
        .attr('y2', height)
        .attr('stroke', '#10b981')
        .attr('stroke-dasharray', '5,5')
        .attr('opacity', 0.7);

      // Poor threshold
      g.append('line')
        .attr('x1', xScale(thresholds.poor))
        .attr('x2', xScale(thresholds.poor))
        .attr('y1', 0)
        .attr('y2', height)
        .attr('stroke', '#ef4444')
        .attr('stroke-dasharray', '5,5')
        .attr('opacity', 0.7);
    }

    // Add bars with color coding
    g.selectAll('.bar')
      .data(bins)
      .enter()
      .append('rect')
      .attr('class', 'bar')
      .attr('x', (d) => xScale(d.x0 || 0))
      .attr('y', (d) => yScale(d.length))
      .attr('width', (d) => Math.max(0, xScale(d.x1 || 0) - xScale(d.x0 || 0) - 1))
      .attr('height', (d) => height - yScale(d.length))
      .attr('fill', (d) => {
        const midpoint = ((d.x0 || 0) + (d.x1 || 0)) / 2;
        const rating = getRatingFromValue(metric, midpoint);
        return getRatingColor(rating);
      })
      .attr('opacity', 0.7)
      .on('mouseover', function () {
        d3.select(this).attr('opacity', 1);
      })
      .on('mouseout', function () {
        d3.select(this).attr('opacity', 0.7);
      });

    // Add labels for thresholds
    if (thresholds) {
      g.append('text')
        .attr('x', xScale(thresholds.good))
        .attr('y', -5)
        .attr('text-anchor', 'middle')
        .attr('font-size', '12px')
        .attr('fill', '#10b981')
        .text('Good');

      g.append('text')
        .attr('x', xScale(thresholds.poor))
        .attr('y', -5)
        .attr('text-anchor', 'middle')
        .attr('font-size', '12px')
        .attr('fill', '#ef4444')
        .text('Poor');
    }
  }, [data, metric]);

  const getMetricThresholds = (metric: string) => {
    const thresholds: Record<string, { good: number; poor: number }> = {
      CLS: { good: 0.1, poor: 0.25 },
      FCP: { good: 1800, poor: 3000 },
      LCP: { good: 2500, poor: 4000 },
      TTFB: { good: 800, poor: 1800 },
      INP: { good: 200, poor: 500 }
    };
    return thresholds[metric];
  };

  const getRatingFromValue = (metric: string, value: number) => {
    const thresholds = getMetricThresholds(metric);
    if (!thresholds) return 'good'; // Default

    if (value <= thresholds.good) {
      return 'good';
    } else if (value <= thresholds.poor) {
      return 'needs-improvement';
    } else {
      return 'poor';
    }
  };

  const getRatingColor = (rating: 'good' | 'needs-improvement' | 'poor') => {
    switch (rating) {
      case 'good':
        return '#10b981';
      case 'needs-improvement':
        return '#f59e0b';
      case 'poor':
        return '#ef4444';
      default:
        return '#6b7280';
    }
  };

  return (
    <div className="w-full overflow-x-auto">
      <svg ref={svgRef} className="w-full h-[300px]" />
    </div>
  );
}

'use client';

import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import type { ChartDataPoint } from '@/types/dashboardTypes';

interface DonutChartProps {
  data: ChartDataPoint[];
}

export default function DonutChart({ data }: DonutChartProps) {
  const svgRef = useRef<SVGSVGElement>(null);

  useEffect(() => {
    if (!data.length || !svgRef.current) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    const width = 300;
    const height = 300;
    const radius = Math.min(width, height) / 2;
    const innerRadius = radius * 0.6;

    const g = svg
      .attr('width', width)
      .attr('height', height)
      .append('g')
      .attr('transform', `translate(${width / 2},${height / 2})`);

    // 등급별 데이터 집계
    const ratingCounts = data.reduce(
      (acc, d) => {
        acc[d.rating] = (acc[d.rating] || 0) + 1;
        return acc;
      },
      {} as Record<'good' | 'needs-improvement' | 'poor', number>
    );

    const chartData = [
      { label: '좋음', value: ratingCounts.good || 0, color: '#10b981' },
      { label: '개선필요', value: ratingCounts['needs-improvement'] || 0, color: '#f59e0b' },
      { label: '나쁨', value: ratingCounts.poor || 0, color: '#ef4444' }
    ].filter((d) => d.value > 0); // 값이 0인 항목은 제외

    if (!chartData.length) {
      g.append('text')
        .attr('x', 0)
        .attr('y', 0)
        .attr('text-anchor', 'middle')
        .attr('fill', '#6b7280')
        .text('데이터가 없습니다.');
      return;
    }

    const pie = d3
      .pie<(typeof chartData)[0]>()
      .value((d) => d.value)
      .sort(null);

    const arc = d3
      .arc<d3.PieArcDatum<(typeof chartData)[0]>>()
      .innerRadius(innerRadius)
      .outerRadius(radius);

    const arcs = g.selectAll('.arc').data(pie(chartData)).enter().append('g').attr('class', 'arc');

    arcs
      .append('path')
      .attr('d', arc)
      .attr('fill', (d) => d.data.color)
      .attr('opacity', 0.8)
      .on('mouseover', function () {
        d3.select(this).attr('opacity', 1);
      })
      .on('mouseout', function () {
        d3.select(this).attr('opacity', 0.8);
      });

    // Add labels
    arcs
      .append('text')
      .attr('transform', (d) => `translate(${arc.centroid(d)})`)
      .attr('text-anchor', 'middle')
      .attr('font-size', '12px')
      .attr('fill', 'white')
      .attr('font-weight', 'bold')
      .each(function (d) {
        const text = d3.select(this);
        const percentage = ((d.data.value / d3.sum(chartData, (d) => d.value)) * 100).toFixed(1);
        text.append('tspan').attr('x', 0).attr('dy', '-0.2em').text(d.data.label);
        text.append('tspan').attr('x', 0).attr('dy', '1.2em').text(`${percentage}%`);
      });

    // Add center text showing total count
    const totalCount = d3.sum(chartData, (d) => d.value);
    g.append('text')
      .attr('text-anchor', 'middle')
      .attr('y', -5)
      .attr('font-size', '24px')
      .attr('font-weight', 'bold')
      .attr('fill', '#333')
      .text(`${totalCount}`);

    g.append('text')
      .attr('text-anchor', 'middle')
      .attr('y', 15)
      .attr('font-size', '12px')
      .attr('fill', '#6b7280')
      .text('총 데이터');
  }, [data]);

  return (
    <div className="flex justify-center">
      <svg ref={svgRef} className="w-[300px] h-[300px]" />
    </div>
  );
}

'use client';

import { useEffect, useRef } from 'react';
import * as d3 from 'd3';

interface GaugeChartProps {
  value: number;
  label: string;
  max: number;
}

export default function GaugeChart({ value, label, max }: GaugeChartProps) {
  const svgRef = useRef<SVGSVGElement>(null);

  useEffect(() => {
    if (!svgRef.current) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    const width = 200;
    const height = 120;
    const radius = 80;

    const g = svg
      .attr('width', width)
      .attr('height', height)
      .append('g')
      .attr('transform', `translate(${width / 2},${height - 10})`);

    // Background arc
    const backgroundArc = d3
      .arc()
      .innerRadius(radius - 20)
      .outerRadius(radius)
      .startAngle(-Math.PI / 2)
      .endAngle(Math.PI / 2);

    g.append('path')
      .attr('d', backgroundArc({} as d3.DefaultArcObject))
      .attr('fill', '#e5e7eb');

    // Value arc
    const valueArc = d3
      .arc()
      .innerRadius(radius - 20)
      .outerRadius(radius)
      .startAngle(-Math.PI / 2)
      .endAngle(-Math.PI / 2 + (Math.PI * value) / max);

    // Color based on performance score
    const color = value >= 80 ? '#10b981' : value >= 50 ? '#f59e0b' : '#ef4444';

    g.append('path')
      .attr('d', valueArc({} as d3.DefaultArcObject))
      .attr('fill', color);

    // Center text
    g.append('text')
      .attr('text-anchor', 'middle')
      .attr('y', -10)
      .attr('font-size', '24px')
      .attr('font-weight', 'bold')
      .attr('fill', color)
      .text(`${Math.round(value)}`);

    g.append('text')
      .attr('text-anchor', 'middle')
      .attr('y', 10)
      .attr('font-size', '12px')
      .attr('fill', '#6b7280')
      .text(label);

    // Add score indicators
    const scoreText = value >= 80 ? '좋음' : value >= 50 ? '보통' : '나쁨';
    g.append('text')
      .attr('text-anchor', 'middle')
      .attr('y', 25)
      .attr('font-size', '10px')
      .attr('fill', color)
      .text(scoreText);
  }, [value, label, max]);

  return (
    <div className="flex justify-center">
      <svg ref={svgRef} className="w-[200px] h-[120px]" />
    </div>
  );
}

'use client';

import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import type { ChartDataPoint } from '@/types/dashboardTypes';

interface LineChartProps {
  data: ChartDataPoint[];
  metric?: 'CLS' | 'FCP' | 'LCP' | 'TTFB' | 'INP';
}

export default function LineChart({ data, metric }: LineChartProps) {
  const svgRef = useRef<SVGSVGElement>(null);

  useEffect(() => {
    if (!data.length || !svgRef.current) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    const margin = { top: 20, right: 30, bottom: 40, left: 60 };
    const width = 600 - margin.left - margin.right;
    const height = 300 - margin.bottom - margin.top;

    const g = svg
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    // Scales
    const xScale = d3
      .scaleTime()
      .domain(d3.extent(data, (d) => d.timestamp) as [Date, Date])
      .range([0, width]);

    let yScale: d3.ScaleLinear<number, number>;
    let lines: {
      key: string;
      color: string;
      accessor: (d: ChartDataPoint) => number;
      label: string;
    }[];

    if (metric) {
      const filteredData = data.filter((d) => d.name === metric);
      if (!filteredData.length) {
        g.append('text')
          .attr('x', width / 2)
          .attr('y', height / 2)
          .attr('text-anchor', 'middle')
          .attr('fill', '#6b7280')
          .text('데이터가 없습니다.');
        return;
      }

      const getValue = (d: ChartDataPoint) => d.value;
      yScale = d3
        .scaleLinear()
        .domain(d3.extent(filteredData, getValue) as [number, number])
        .nice()
        .range([height, 0]);

      lines = [
        {
          key: metric,
          color: getMetricColor(metric),
          accessor: getValue,
          label: getMetricLabel(metric)
        }
      ];
    } else {
      // 전체 개요에서는 각 메트릭별 데이터를 필터링하여 사용
      const metricNames: ('CLS' | 'FCP' | 'LCP' | 'TTFB' | 'INP')[] = [
        'CLS',
        'FCP',
        'LCP',
        'TTFB',
        'INP'
      ];
      yScale = d3.scaleLinear().domain([0, 4000]).range([height, 0]); // 모든 메트릭을 포함할 수 있는 적절한 범위

      lines = metricNames.map((name) => ({
        key: name,
        color: getMetricColor(name),
        accessor: (d) => d.value, // 실제 값 사용
        label: getMetricLabel(name)
      }));
    }

    // Add axes
    g.append('g')
      .attr('transform', `translate(0,${height})`)
      .call(
        d3.axisBottom(xScale).tickFormat((domainValue: Date | d3.NumberValue) => {
          // domainValue가 Date 타입이면 포맷 적용, 아니면 빈 문자열 반환
          if (domainValue instanceof Date) {
            return d3.timeFormat('%H:%M')(domainValue);
          }
          // d3.scaleTime이 아닌 경우에도 대비
          return '';
        })
      );

    const yAxis =
      metric === 'CLS'
        ? d3.axisLeft(yScale).tickFormat(d3.format('.3f'))
        : metric && ['FCP', 'LCP', 'TTFB', 'INP'].includes(metric)
          ? d3.axisLeft(yScale).tickFormat((d) => `${d}ms`)
          : d3.axisLeft(yScale).tickFormat((d) => `${d}ms`); // 전체 개요에서도 ms 단위로 표시

    g.append('g').call(yAxis);

    // Add threshold lines for single metrics
    if (metric) {
      const thresholds = getMetricThresholds(metric);
      if (thresholds) {
        // Good threshold
        g.append('line')
          .attr('x1', 0)
          .attr('x2', width)
          .attr('y1', yScale(thresholds.good))
          .attr('y2', yScale(thresholds.good))
          .attr('stroke', '#10b981')
          .attr('stroke-dasharray', '5,5')
          .attr('opacity', 0.7);

        // Poor threshold
        g.append('line')
          .attr('x1', 0)
          .attr('x2', width)
          .attr('y1', yScale(thresholds.poor))
          .attr('y2', yScale(thresholds.poor))
          .attr('stroke', '#ef4444')
          .attr('stroke-dasharray', '5,5')
          .attr('opacity', 0.7);
      }
    }

    // Add lines
    lines.forEach((lineConfig) => {
      const lineData = data.filter((d) => d.name === lineConfig.key);
      if (!lineData.length) return;

      const lineGenerator = d3
        .line<ChartDataPoint>()
        .x((d) => xScale(d.timestamp))
        .y((d) => yScale(lineConfig.accessor(d)))
        .curve(d3.curveMonotoneX);

      g.append('path')
        .datum(lineData)
        .attr('fill', 'none')
        .attr('stroke', lineConfig.color)
        .attr('stroke-width', 2)
        .attr('d', lineGenerator);

      // Add dots
      g.selectAll(`.dot-${lineConfig.key}`)
        .data(lineData)
        .enter()
        .append('circle')
        .attr('class', `dot-${lineConfig.key}`)
        .attr('cx', (d) => xScale(d.timestamp))
        .attr('cy', (d) => yScale(lineConfig.accessor(d)))
        .attr('r', 2)
        .attr('fill', lineConfig.color);
    });

    // Add grid lines
    g.append('g')
      .attr('class', 'grid')
      .attr('transform', `translate(0,${height})`)
      .call(
        d3
          .axisBottom(xScale)
          .tickSize(-height)
          .tickFormat(() => '')
      )
      .style('stroke-dasharray', '3,3')
      .style('opacity', 0.3);

    g.append('g')
      .attr('class', 'grid')
      .call(
        d3
          .axisLeft(yScale)
          .tickSize(-width)
          .tickFormat(() => '')
      )
      .style('stroke-dasharray', '3,3')
      .style('opacity', 0.3);
  }, [data, metric]);

  const getMetricColor = (metric: string) => {
    const colors: Record<string, string> = {
      CLS: '#ef4444',
      FCP: '#3b82f6',
      LCP: '#10b981',
      TTFB: '#f59e0b',
      INP: '#8b5cf6'
    };
    return colors[metric] || '#6b7280';
  };

  const getMetricLabel = (metric: string) => {
    const labels: Record<string, string> = {
      CLS: 'CLS',
      FCP: 'FCP',
      LCP: 'LCP',
      TTFB: 'TTFB',
      INP: 'INP'
    };
    return labels[metric] || metric;
  };

  const getMetricThresholds = (metric: string) => {
    const thresholds: Record<string, { good: number; poor: number }> = {
      CLS: { good: 0.1, poor: 0.25 },
      FCP: { good: 1800, poor: 3000 },
      LCP: { good: 2500, poor: 4000 },
      TTFB: { good: 800, poor: 1800 },
      INP: { good: 200, poor: 500 }
    };
    return thresholds[metric];
  };

  return (
    <div className="w-full overflow-x-auto">
      <svg ref={svgRef} className="w-full h-[300px]" />
    </div>
  );
}

